import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Grid,
  Chip,
  IconButton,
  Tooltip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  CircularProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination
} from '@mui/material';
import {
  Add as AddIcon,
  Receipt as ReceiptIcon,
  Refresh as RefreshIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  Visibility as VisibilityIcon
} from '@mui/icons-material';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';

interface Venta {
  id_venta: number;
  fecha_venta: string;
  hora_entrada: string;
  hora_salida?: string;
  tiempo_total?: number;
  tarifa_hora: number;
  monto_total?: number;
  estado: 'activa' | 'finalizada' | 'cancelada';
  id_cliente?: number;
  id_espacio: number;
  id_usuario: number;
  placa_vehiculo: string;
  tipo_vehiculo: string;
  observaciones?: string;
  observaciones_salida?: string;
  motivo_cancelacion?: string;
  // Campos relacionados
  nombre_cliente?: string;
  apellido_cliente?: string;
  numero_espacio?: string;
  nombre_usuario?: string;
}

interface VentaForm {
  id_espacio: number;
  id_cliente?: number;
  placa_vehiculo: string;
  tipo_vehiculo: string;
  observaciones: string;
}

interface Espacio {
  id_espacio: number;
  numero_espacio: string;
  tipo_vehiculo: string;
  tarifa_hora: number;
  estado: string;
}

interface Cliente {
  idCliente: number;
  Nombre_Cliente: string;
  Apellido_Cliente: string;
  ruc_Cliente?: string;
}

const Ventas: React.FC = () => {

  const [ventas, setVentas] = useState<Venta[]>([]);
  const [espacios, setEspacios] = useState<Espacio[]>([]);
  const [clientes, setClientes] = useState<Cliente[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openFinalizarDialog, setOpenFinalizarDialog] = useState(false);
  const [openCancelarDialog, setOpenCancelarDialog] = useState(false);
  const [selectedVenta, setSelectedVenta] = useState<Venta | null>(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');

  const [formData, setFormData] = useState<VentaForm>({
    id_espacio: 0,
    id_cliente: undefined,
    placa_vehiculo: '',
    tipo_vehiculo: 'auto',
    observaciones: ''
  });

  const [finalizarForm, setFinalizarForm] = useState({
    observaciones_salida: ''
  });

  const [cancelarForm, setCancelarForm] = useState({
    motivo_cancelacion: ''
  });

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [ventasResponse, espaciosResponse, clientesResponse] = await Promise.all([
        axios.get('/api/ventas'),
        axios.get('/api/espacios/libres'),
        axios.get('/api/clientes')
      ]);

      console.log('Ventas recibidas:', ventasResponse.data.data);
      console.log('Espacios recibidos:', espaciosResponse.data.data);
      console.log('Clientes recibidos:', clientesResponse.data.data);
      
      setVentas(ventasResponse.data.data);
      setEspacios(espaciosResponse.data.data);
      setClientes(clientesResponse.data.data);
    } catch (error: any) {
      console.error('Error al cargar datos:', error);
      setError('Error al cargar los datos');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleOpenDialog = () => {
    setFormData({
      id_espacio: 0,
      id_cliente: undefined,
      placa_vehiculo: '',
      tipo_vehiculo: 'auto',
      observaciones: ''
    });
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setFormData({
      id_espacio: 0,
      id_cliente: undefined,
      placa_vehiculo: '',
      tipo_vehiculo: 'auto',
      observaciones: ''
    });
  };

  const handleSubmit = async () => {
    try {
      if (!formData.id_espacio || !formData.placa_vehiculo) {
        setError('Por favor complete todos los campos requeridos');
        return;
      }

      await axios.post('/api/ventas', formData);
      fetchData();
      handleCloseDialog();
    } catch (error: any) {
      console.error('Error al crear venta:', error);
      setError(error.response?.data?.error || 'Error al crear la venta');
    }
  };

  const handleFinalizar = async () => {
    if (!selectedVenta) return;
    
    try {
      await axios.put(`/api/ventas/${selectedVenta.id_venta}/finalizar`, finalizarForm);
      fetchData();
      setOpenFinalizarDialog(false);
      setSelectedVenta(null);
      setFinalizarForm({ observaciones_salida: '' });
    } catch (error: any) {
      console.error('Error al finalizar venta:', error);
      setError(error.response?.data?.error || 'Error al finalizar la venta');
    }
  };

  const handleCancelar = async () => {
    if (!selectedVenta) return;
    
    try {
      await axios.put(`/api/ventas/${selectedVenta.id_venta}/cancelar`, cancelarForm);
      fetchData();
      setOpenCancelarDialog(false);
      setSelectedVenta(null);
      setCancelarForm({ motivo_cancelacion: '' });
    } catch (error: any) {
      console.error('Error al cancelar venta:', error);
      setError(error.response?.data?.error || 'Error al cancelar la venta');
    }
  };

  const openFinalizarVenta = (venta: Venta) => {
    setSelectedVenta(venta);
    setOpenFinalizarDialog(true);
  };

  const openCancelarVenta = (venta: Venta) => {
    setSelectedVenta(venta);
    setOpenCancelarDialog(true);
  };

  const filteredVentas = ventas.filter(venta => {
    // Validar que la venta tenga los campos bÃ¡sicos
    if (!venta || !venta.placa_vehiculo || !venta.tipo_vehiculo) {
      console.warn('Venta con campos faltantes:', venta);
      return false;
    }
    
    return (
      (venta.placa_vehiculo || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
      (venta.numero_espacio || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
      (venta.nombre_cliente || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
      (venta.apellido_cliente || '').toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const paginatedVentas = filteredVentas.slice(
    page * rowsPerPage,
    page * rowsPerPage + rowsPerPage
  );

  const getStatusColor = (estado: string) => {
    switch (estado) {
      case 'activa':
        return 'warning';
      case 'finalizada':
        return 'success';
      case 'cancelada':
        return 'error';
      default:
        return 'default';
    }
  };

  const getStatusLabel = (estado: string) => {
    switch (estado) {
      case 'activa':
        return 'Activa';
      case 'finalizada':
        return 'Finalizada';
      case 'cancelada':
        return 'Cancelada';
      default:
        return estado;
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN'
    }).format(amount);
  };



  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress size={60} />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <Box>
          <Typography variant="h4" component="h1" gutterBottom sx={{ color: '#1976d2', fontWeight: 600 }}>
            ð° GestiÃ³n de Ventas
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Administra las ventas y facturaciÃ³n del sistema
          </Typography>
        </Box>
        
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpenDialog}
          sx={{ bgcolor: '#1976d2' }}
        >
          Nueva Venta
        </Button>
      </Box>

      {/* EstadÃ­sticas rÃ¡pidas */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h4" color="primary">
                {ventas.length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total de Ventas
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h4" color="warning.main">
                {ventas.filter(v => v.estado === 'activa').length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Ventas Activas
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h4" color="success.main">
                {ventas.filter(v => v.estado === 'finalizada').length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Ventas Finalizadas
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h4" color="error.main">
                {ventas.filter(v => v.estado === 'cancelada').length}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Ventas Canceladas
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Mensaje de error */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {/* Filtros y bÃºsqueda */}
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                placeholder="Buscar por placa, espacio o cliente..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                InputProps={{
                  startAdornment: <ReceiptIcon sx={{ mr: 1, color: 'text.secondary' }} />
                }}
              />
            </Grid>
            <Grid item xs={12} md={6} textAlign="right">
              <Button
                startIcon={<RefreshIcon />}
                onClick={fetchData}
                variant="outlined"
              >
                Actualizar
              </Button>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Tabla de ventas */}
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom sx={{ mb: 3 }}>
            Lista de Ventas
          </Typography>

          {paginatedVentas.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4, color: 'text.secondary' }}>
              <ReceiptIcon sx={{ fontSize: 60, mb: 2, opacity: 0.3 }} />
              <Typography variant="h6" gutterBottom>
                No hay ventas registradas
              </Typography>
              <Typography variant="body2">
                Comienza creando la primera venta de parqueamiento.
              </Typography>
            </Box>
          ) : (
            <>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>ID</TableCell>
                      <TableCell>Fecha</TableCell>
                      <TableCell>Espacio</TableCell>
                      <TableCell>VehÃ­culo</TableCell>
                      <TableCell>Cliente</TableCell>
                      <TableCell>Estado</TableCell>
                      <TableCell>Monto</TableCell>
                      <TableCell>Acciones</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {paginatedVentas.map((venta) => (
                      <TableRow key={venta.id_venta} hover>
                        <TableCell>
                          <Typography variant="body2" fontWeight={600}>
                            #{venta.id_venta}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">
                            {new Date(venta.fecha_venta).toLocaleDateString()}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {new Date(venta.hora_entrada).toLocaleTimeString()}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip 
                            label={venta.numero_espacio || 'N/A'} 
                            color="primary" 
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          <Box>
                            <Typography variant="body2" fontWeight={600}>
                              {venta.placa_vehiculo}
                            </Typography>
                                                         <Chip 
                               label={venta.tipo_vehiculo || 'N/A'} 
                               color="secondary" 
                               size="small"
                             />
                          </Box>
                        </TableCell>
                        <TableCell>
                          {venta.nombre_cliente ? (
                            <Typography variant="body2">
                              {venta.nombre_cliente} {venta.apellido_cliente}
                            </Typography>
                          ) : (
                            <Typography variant="body2" color="text.secondary">
                              Sin cliente
                            </Typography>
                          )}
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={getStatusLabel(venta.estado)}
                            color={getStatusColor(venta.estado) as any}
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          {venta.monto_total ? (
                            <Typography variant="body2" fontWeight={600} color="success.main">
                              {formatCurrency(venta.monto_total)}
                            </Typography>
                          ) : (
                            <Typography variant="body2" color="text.secondary">
                              Pendiente
                            </Typography>
                          )}
                        </TableCell>
                        <TableCell>
                          <Box sx={{ display: 'flex', gap: 1 }}>
                            <Tooltip title="Ver detalles">
                              <IconButton size="small" color="primary">
                                <VisibilityIcon />
                              </IconButton>
                            </Tooltip>
                            
                            {venta.estado === 'activa' && (
                              <>
                                <Tooltip title="Finalizar venta">
                                  <IconButton 
                                    size="small" 
                                    color="success"
                                    onClick={() => openFinalizarVenta(venta)}
                                  >
                                    <CheckCircleIcon />
                                  </IconButton>
                                </Tooltip>
                                
                                <Tooltip title="Cancelar venta">
                                  <IconButton 
                                    size="small" 
                                    color="error"
                                    onClick={() => openCancelarVenta(venta)}
                                  >
                                    <CancelIcon />
                                  </IconButton>
                                </Tooltip>
                              </>
                            )}
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
              
              <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                component="div"
                count={filteredVentas.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={(_, newPage) => setPage(newPage)}
                onRowsPerPageChange={(e) => {
                  setRowsPerPage(parseInt(e.target.value, 10));
                  setPage(0);
                }}
              />
            </>
          )}
        </CardContent>
      </Card>

      {/* Dialog para crear nueva venta */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <AddIcon color="primary" />
            Nueva Venta de Parqueamiento
          </Box>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth required>
                <InputLabel>Espacio</InputLabel>
                <Select
                  value={formData.id_espacio}
                  label="Espacio"
                  onChange={(e) => setFormData({ ...formData, id_espacio: e.target.value as number })}
                >
                  {espacios.map((espacio) => (
                    <MenuItem key={espacio.id_espacio} value={espacio.id_espacio}>
                      {espacio.numero_espacio} - {espacio.tipo_vehiculo} (S/ {typeof espacio.tarifa_hora === 'number' ? espacio.tarifa_hora : 0}/h)
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>Cliente (Opcional)</InputLabel>
                <Select
                  value={formData.id_cliente || ''}
                  label="Cliente (Opcional)"
                  onChange={(e) => setFormData({ ...formData, id_cliente: e.target.value as number || undefined })}
                >
                  <MenuItem value="">Sin cliente</MenuItem>
                  {clientes.map((cliente) => (
                    <MenuItem key={cliente.idCliente} value={cliente.idCliente}>
                      {cliente.Nombre_Cliente} {cliente.Apellido_Cliente}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Placa del VehÃ­culo *"
                value={formData.placa_vehiculo}
                onChange={(e) => setFormData({ ...formData, placa_vehiculo: e.target.value.toUpperCase() })}
                required
                placeholder="ABC-123"
              />
            </Grid>
            
            <Grid item xs={12} md={6}>
              <FormControl fullWidth required>
                <InputLabel>Tipo de VehÃ­culo</InputLabel>
                <Select
                  value={formData.tipo_vehiculo}
                  label="Tipo de VehÃ­culo"
                  onChange={(e) => setFormData({ ...formData, tipo_vehiculo: e.target.value as string })}
                >
                  <MenuItem value="auto">Auto</MenuItem>
                  <MenuItem value="camioneta">Camioneta</MenuItem>
                  <MenuItem value="camion">CamiÃ³n</MenuItem>
                  <MenuItem value="moto">Moto</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Observaciones"
                value={formData.observaciones}
                onChange={(e) => setFormData({ ...formData, observaciones: e.target.value })}
                multiline
                rows={3}
                placeholder="Observaciones adicionales sobre la venta..."
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancelar</Button>
          <Button onClick={handleSubmit} variant="contained" sx={{ bgcolor: '#1976d2' }}>
            Crear Venta
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog para finalizar venta */}
      <Dialog open={openFinalizarDialog} onClose={() => setOpenFinalizarDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <CheckCircleIcon color="success" />
            Finalizar Venta #{selectedVenta?.id_venta}
          </Box>
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Typography variant="body1" gutterBottom>
              Â¿EstÃ¡s seguro de que quieres finalizar esta venta?
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Se calcularÃ¡ el monto total basado en el tiempo transcurrido.
            </Typography>
            
            <TextField
              fullWidth
              label="Observaciones de Salida"
              value={finalizarForm.observaciones_salida}
              onChange={(e) => setFinalizarForm({ observaciones_salida: e.target.value })}
              multiline
              rows={3}
              placeholder="Observaciones sobre la salida del vehÃ­culo..."
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenFinalizarDialog(false)}>Cancelar</Button>
          <Button onClick={handleFinalizar} variant="contained" color="success">
            Finalizar Venta
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog para cancelar venta */}
      <Dialog open={openCancelarDialog} onClose={() => setOpenCancelarDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <CancelIcon color="error" />
            Cancelar Venta #{selectedVenta?.id_venta}
          </Box>
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Typography variant="body1" gutterBottom>
              Â¿EstÃ¡s seguro de que quieres cancelar esta venta?
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Esta acciÃ³n liberarÃ¡ el espacio de parqueamiento.
            </Typography>
            
            <TextField
              fullWidth
              label="Motivo de CancelaciÃ³n *"
              value={cancelarForm.motivo_cancelacion}
              onChange={(e) => setCancelarForm({ motivo_cancelacion: e.target.value })}
              multiline
              rows={3}
              placeholder="Especifica el motivo de la cancelaciÃ³n..."
              required
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenCancelarDialog(false)}>Cancelar</Button>
          <Button onClick={handleCancelar} variant="contained" color="error">
            Cancelar Venta
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Ventas;
